CREATE DATABASE IF NOT EXISTS iroha_hyakunin_isshu;
USE iroha_hyakunin_isshu;

CREATE TABLE waka (
    waka_id INT PRIMARY KEY,
    kami_no_ku VARCHAR(255) NOT NULL,
    kami_no_ku_yomi VARCHAR(255) NOT NULL,
    shimo_no_ku VARCHAR(255) NOT NULL,
    shimo_no_ku_yomi VARCHAR(255) NOT NULL,
    sakusha VARCHAR(255) NOT NULL,
    sakusha_yomi VARCHAR(255) NOT NULL,
    kaisetsu TEXT,
    haikei TEXT
);

INSERT INTO waka (waka_id, kami_no_ku, kami_no_ku_yomi, shimo_no_ku, shimo_no_ku_yomi, sakusha, sakusha_yomi, kaisetsu, haikei) VALUES
(1, '秋の田のかりほの庵の苫をあらみ', 'あきのたのかりほのいおのとまをあらみ', 'わが衣手は露にぬれつつ', 'わがころもではつゆにぬれつつ', '天智天皇', 'てんじてんのう', '秋の田の仮小屋に泊まっていると、苫の目が粗いので、私の袖は夜露でびっしょり濡れてしまう。', '天智天皇が大津宮を営んでいた頃、稲刈りの終わった田んぼの仮小屋に泊まって稲作の苦労をしのび、五穀豊穣を願って詠んだ歌と伝えられています。'),
(2, '春過ぎて夏来にけらし白妙の', 'はるすぎてなつきにけらししろたえの', '衣干すてふ天の香具山', 'ころもほすちょうあまのかぐやま', '持統天皇', 'じとうてんのう', '春が過ぎて夏が来たらしい。純白の衣を干しているという天の香具山が見えることだ。', '桜の花が散り、青葉が茂る頃に詠まれた歌。奈良の藤原宮から、遠くに見える天の香具山に白い衣が干してあるのを見て、季節の移り変わりを感じた情景を詠んでいます。'),
(3, 'あしびきの山鳥の尾のしだり尾の', 'あしびきのやまとりのおのしだりおの', 'ながながし夜をひとりかも寝む', 'ながながしよをひとりかもねん', '柿本人麻呂', 'かきのもとのひとまろ', '山鳥の尾のように長く垂れ下がった、その長い長い夜を私は一人で寝ることになるのだろうか。', '作者が旅の途中で、妻と離れて寂しい夜を過ごしている様子を詠んだ歌です。山鳥が尾を垂らす姿から、夜が長く感じられる情景を重ね合わせています。'),
(4, '田子の浦にうち出でて見れば白妙の', 'たごのうらにうちいでてみればしろたえの', '富士の高嶺に雪は降りつつ', 'ふじのたかねにゆきはふりつつ', '山部赤人', 'やまべのあかひと', '田子の浦に出て見渡すと、真っ白な富士の高い峰に雪が降り積もっている。', '奈良時代、富士山の雄大な景色を詠んだ歌です。田子の浦から見た富士山が、純白の雪をかぶっている様子を美しく描写しています。'),
(5, '奥山に紅葉踏み分け鳴く鹿の', 'おくやまにもみじふみわけなくしかの', '声聞く時ぞ秋は悲しき', 'こえきくときぞあきはかなしき', '猿丸大夫', 'さるまるだゆう', '奥山に紅葉を踏み分けて鳴いている鹿の声を聞く時こそ、秋という季節は悲しいものだと心から感じる。', '作者不詳ですが、奥山で紅葉を踏み分けながら妻を求めて鳴く鹿の声を聞き、孤独と悲哀を感じる秋の情景を詠んだ歌です。'),
(6, '鵲の渡せる橋に置く霜の', 'かささぎのわたせるはしにおくしもの', 'しろきを見れば夜ぞ更けにける', 'しろきをみればよぞふけにける', '中納言家持', 'ちゅうなごんやかもち', '宮中にある鵲（かささぎ）が渡すという橋に、降り積もった霜が白く光っているのを見ると、夜がもうずいぶん更けたのだなぁ。', '七夕伝説に由来する歌です。天の川に鵲が翼を並べて橋をかけるという伝説と、宮中の庭にかかる階段を「鵲の橋」に見立て、冬の寒い夜、霜が降りて白くなった階段を見て夜の更けを感じる情景を詠んだ歌です。'),
(7, '天の原ふりさけ見れば春日なる', 'あまのはらふりさけみればかすがなる', '三笠の山に出でし月かも', 'みかさのやまにいでしつきかも', '安倍仲麻呂', 'あべのなかまろ', '大空を遠く見上げると、故郷の春日にある三笠の山から昇った月と同じ月が見える。', '遣唐使として唐に渡った安倍仲麻呂が、帰国を目前に詠んだ望郷の歌です。唐の地で、故郷の奈良の春日にある三笠山から昇る月を遠い故郷の友や家族を思いながら見た感動を詠んでいます。'),
(8, '我が庵は都のたつみしかぞ住む', 'わがいおはみやこのたつみしかぞすむ', '世をうぢ山と人はいふなり', 'よをうじやまとひとはいうなり', '喜撰法師', 'きせんほうし', '私の住む庵は都の辰巳（南東）の方向、宇治山にあり、世を憂い仏道修行に励んでいると人は言うらしい。', '作者は謎の多い人物で、真偽は不明ですが、俗世を離れて暮らす自身の隠遁生活を自嘲的に詠んだ歌と解釈されています。「憂し（うし）」と「宇治（うじ）」をかけた地名にかけた歌です。'),
(9, '花の色は移りにけりないたづらに', 'はなのいろはうつりにけりないたづらに', '我が身世にふるながめせしまに', 'わがみよにふるながめせしまに', '小野小町', 'おののこまち', '桜の花の色は、むなしくも色あせてしまったなぁ。ぼんやりと物思いにふけって長雨を眺めているうちに。まるで私の人生のように。', '作者が衰えた自身の美しさを桜の花に重ねて詠んだ歌です。花が雨に濡れて色あせる様と、自身の美貌が衰えてしまった様を重ね合わせています。'),
(10, 'これやこの行くも帰るも別れては', 'これやこのゆくもかえるもわかれては', '知るも知らぬも逢坂の関', 'しるもしらぬもあふさかのせき', '蝉丸', 'せみまる', 'ここが、都へ行く人も都から帰る人も、知り合いも知らない人も、皆、分かれまた出会うという逢坂の関なのだな。', '逢坂の関で人々が行き交う様子を詠んだ歌です。京都と地方を結ぶ関所として、多くの人々が別れや出会いを経験する場所であったことを表現しています。'),
(11, 'わたの原八十島かけて漕ぎ出でぬと', 'わたのはらやそしまかけてこぎいでぬと', '人には告げよ天の釣舟', 'ひとにはつげよあまのつりぶね', '参議篁', 'さんぎたかむら', '広々とした海原をたくさんの島々をさして船を漕ぎ出していくと、都の人に告げておくれ、空の釣り船よ。', '作者が隠岐島へ流される際に詠んだ歌とされています。天に浮かぶ釣り船に自分の境遇を告げるよう頼み、海を渡って流されていく自身の姿を雄大に表現しています。'),
(12, '天つ風雲の通ひ路吹きとぢよ', 'あまつかぜくものかよいじふきとじよ', 'をとめの姿しばしとどめむ', 'おとめのすがたしばしとどめん', '僧正遍昭', 'そうじょうへんじょう', '天を吹く風よ、雲の中の通り道を塞いでしまえ。天女たちの姿をもう少しの間、この世に留めておきたいのだから。', '平安初期の宮中の儀式「五節の舞」を観て詠まれた歌。舞姫の優雅な姿を天女に見立て、その美しさがいつまでも続いてほしいという願いを表現しています。'),
-- 以下を確認する。
(13, '筑波嶺の峰より落つるみなの川', 'つくばねのみねよりおつるみなのがわ', '恋ぞつもりて淵となりぬる', 'こいぞつもりてふちとなりぬる', '陽成院', 'ようぜいいん', '筑波山の峰から流れ落ちる水の流れが、やがて湊川（みなのがわ）となって深い淵を作るように、私の恋心も積もり積もって深い淵のようになってしまった。', '恋の歌として詠まれました。小さな水の流れが大きな川となり深い淵をなすように、初めはささいな恋心だったのが、次第に深く大きくなっていったことを表現しています。'),
(14, 'みちのくのしのぶもぢずり誰ゆえに', 'みちのくのしのぶもじずりたれゆえに', '乱れそめにし我ならなくに', 'みだれそめにしわれならなくに', '河原左大臣', 'かわらのさだいじん', '陸奥国の忍草で染めた模様のように、私の心が乱れてしまったのは、いったい誰のせいだろうか。私のせいではないのに。', '恋の歌として詠まれました。忍草の染め物（忍ぶもぢずり）の複雑で乱れた模様と、誰かのせいで乱れてしまった自身の心を重ねて表現しています。'),
(15, '君がため春の野に出でて若菜摘む', 'きみがためはるののにいでてわかなつむ', '我が衣手に雪は降りつつ', 'わがころもでにゆきはふりつつ', '光孝天皇', 'こうこうてんのう', 'あなたのために春の野に出て若菜を摘んでいると、私の衣の袖には雪が降ってくることだ。', 'まだ皇子だった頃の歌とされています。大切な人のために雪が降る中でも若菜を摘む、ひたむきで優しい心を表現しています。'),
(16, '立ち別れいなばの山の峰に生ふる', 'たちわかれいなばのやまのみねにおうる', 'まつとし聞かば今帰り来む', 'まつとしきかばいまかえりこん', '中納言行平', 'ちゅうなごんゆきひら', '（任地へ向かうために）あなたと別れて稲羽の山を越えて行くけれど、あなたが私を「待つ」と聞いたなら、すぐにでも帰ってこよう。', '任地である因幡国（いなばのくに）へ旅立つ際に詠まれた歌です。「因幡（いなば）」と「往（い）なば」、「待つ」と「松（まつ）」をかけた、言葉遊びの巧みな歌です。'),
(17, 'ちはやぶる神代も聞かず竜田川', 'ちはやぶるかみよもきかずたつたがわ', 'から紅に水くくるとは', 'からくれないにみずくくるとは', '在原業平朝臣', 'ありわらのなりひらあそん', '神々が荒々しい力を持っていた神代の昔でさえ聞いたことがない、竜田川が真っ赤に水をくくり染めにしているとは。', '紅葉の名所として有名な竜田川の情景を詠んだ歌です。川面を埋め尽くすほどの紅葉を、美しいくくり染めの布に見立てた、作者の卓越した感性を表しています。'),
(18, '住の江の岸に寄る波よるさへや', 'すみのえのきしによるなみよるさえや', '夢の通ひ路人目よくらむ', 'ゆめのかよいじひとめよくらん', '藤原敏行朝臣', 'ふじわらのとしゆきあそん', '住の江の岸に夜も波が寄せるように、あなたと夢の中で会っているのに、人目を避けるようにこっそりと通っているのでしょうか。', '恋の歌として詠まれた歌です。「住の江の岸に寄る波」と「夜（よる）」を、「夢の通ひ路」と「人目よくらむ」を対比させて、夜ですら人目を気にして逢えない苦しさを表現しています。'),
(19, '難波潟短き葦のふしの間も', 'なにわがたみじかきあしのふしのまも', '逢はでこの世を過ぐしてよとや', 'あわでこのよをすぐしてよとや', '伊勢', 'いせ', '難波潟の短い葦の節と節の間のような、ほんのわずかな時間も会わずにこの世を過ごせというのか。', '恋の歌として詠まれた歌です。「難波潟（なにわがた）」と「逢わ（あわ）」ず、そして「葦（あし）」の「節（ふし）」と「逢ふし（あうし）」をかけて、恋しい人に会えない苦しさを詠んでいます。'),
(20, 'わびぬれば今はた同じ難波なる', 'わびぬればいまはたおなじなにわなる', 'みをつくしても逢はむとぞ思ふ', 'みをつくしてもあわんとぞおもふ', '元良親王', 'もとよししんのう', 'もうすっかり辛くなってしまったので、もはやどうなっても同じだ。難波の澪標（みおつくし）のように、我が身を滅ぼしてでもあなたに逢おうと思う。', '恋の歌として詠まれた歌です。「難波」と「澪標（みおつくし）」をかけ、「みをつくし」と「身を尽くし」を重ねて、恋のために身を滅ぼしても構わないという強い決意を表現しています。'),
(21, '今来むといひしばかりに長月の', 'いまこんといいしばかりにながつきの', '有明の月を待ち出でつるかな', 'ありあけのつきをまちいでつるかな', '素性法師', 'そせいほうし', '「今すぐ来ます」とあなたが言った、ただその言葉を信じたばかりに、九月の夜長を有明の月が出るまで待ち明かしてしまったことだなぁ。', '恋の歌として詠まれた歌です。恋人の「今行く」という言葉を信じて一晩中待ち続けた、切なくもひたむきな思いを表現しています。'),
(22, '吹くからに秋の草木のしをるれば', 'ふくからにあきのくさきのしおるれば', 'むべ山風を嵐といふらむ', 'むべやまかぜをあらしというらん', '文屋康秀', 'ふんやのやすひで', '吹くとすぐに秋の草木がしおれてしまうから、なるほど、山から吹く風を「嵐」というのだろう。', '作者は、秋風が草木を枯らす様子を見て、「荒らし」という言葉を連想し、山から吹く風を「嵐（あらし）」と呼ぶ由来に納得した様子を詠んでいます。言葉遊びの歌として有名です。'),
(23, '月見ればちぢにものこそ悲しけれ', 'つきみればちぢにものこそかなしけれ', '我が身ひとつの秋にはあらねど', 'わがみひとつのあきにはあらねど', '大江千里', 'おおえのちさと', '月を見ると、あれこれと様々なことが悲しく感じられるなぁ。私一人のための秋というわけではないのに。', '月を見て物思いにふける秋の情景を詠んだ歌です。秋の月は誰にとっても美しいが、自分だけが特別に悲しい気持ちになるわけではない、という客観的な視点を交えて、自分の感情を深く表現しています。'),
(24, 'このたびは幣もとりあへず手向山', 'このたびはぬさもとりあえずたむけやま', '紅葉の錦神のまにまに', 'もみじのにしきかみのまにまに', '菅家', 'かんけ', 'この旅では急なことだったので、お供えする幣（ぬさ）も用意できませんでした。手向山の紅葉を美しい錦のようにお捧げしますので、神様のお心のままにお受け取りください。', '菅原道真が宇多天皇の吉野行幸に随行した際、手向山で神に捧げる幣を忘れてしまった時に詠んだ歌とされています。鮮やかな紅葉を幣に見立てることで、機知に富んだ美しい歌になっています。'),
(25, '名にし負はば逢坂山のさねかづら', 'なにしおわばおうさかやまのさねかずら', '人に知られで来るよしもがな', 'ひとにしられでくるよしもがな', '三条右大臣', 'さんじょうのうだいじん', '', ''),
(26, '小倉山峰のもみぢ葉心あらば', 'おぐらやまみねのもみじはこころあらば', '今ひとたびの御幸待たなむ', 'いまひとたびのみゆきまたなん', '貞信公', 'ていしんこう', '小倉山の峰の紅葉の葉よ、もし心があるのなら、もう一度、天皇のお出かけを待って散らずに残っていておくれ。', '醍醐天皇の代に、作者が小倉山に別荘を営んでいた際、紅葉が美しいので天皇を招こうとした歌とされています。紅葉に心があるかのように語りかけ、美しさを留めてほしいと願う心情を表現しています。'),
(27, 'みかの原わきて流るるいづみ川', 'みかのはらわきてながるるいずみがわ', 'いつ見きとてか恋しかるらむ', 'いつみきとてかこひしかるらん', '中納言兼輔', 'ちゅうなごんかねすけ', '御香川（みかのがわ）から流れ出てくる泉川よ。どうして「いつ見か」といって、それほど恋しいと思うのだろうか。', '恋の歌として詠まれました。「みかのはら」と「いづみ川」を使い、「いつ見き」と「泉川」を重ねて、「いつ会ったからといって、こんなにも恋しいのだろうか」という、会ったこともない人への憧れのような恋心を詠んでいます。'),
(28, '山里は冬ぞさびしさまさりける', 'やまざとはふゆぞさびしさまさりける', '人目も草もかれぬと思へば', 'ひとめもくさもかれんとおもえば', '源宗于朝臣', 'みなもとのむねゆきあそん', '山里は冬になると寂しさが一層増すなぁ。人々の訪れもなくなり、草木も枯れてしまうのだと思うと。', '冬の山里の寂しさを詠んだ歌です。「人目も草もかれぬ」という表現で、「人の足跡が絶えて、草も枯れてしまう」という、冬の訪れと人恋しい気持ちを重ねて表現しています。'),
(29, '心あてに折らばや折らむ初霜の', 'こころあてにおらばやおらんはつしもの', '置きまどはせる白菊の花', 'おきまどわせるしらぎくのはな', '凡河内躬恒', 'おおしこうちのみつね', '当てずっぽうに手折ろうとしても折ることができるだろうか。初霜が降りて、どこにあるか見分けがつかなくなってしまった白菊の花を。', '初霜が降りた朝、白くなった庭で、霜と白菊の花を見分けるのが難しい様子を詠んだ歌です。まるで恋人の心を見分けるのが難しいように、見分けのつかない状況を表現しています。'),
(30, '有明のつれなく見えし別れより', 'ありあけのつれなくみえしわかれより', '暁ばかり憂きものはなし', 'あかつきばかりうきものはなし', '壬生忠岑', 'みぶのただみね', '夜が明けていく頃、冷たい態度で別れたあの時から、暁ほどつらいものはない。', '恋の歌として詠まれた歌です。明け方の別れは特に辛いものですが、その中でも相手の態度が冷たかったことが、夜明けの時間をさらに悲しいものに感じさせている様子を表現しています。'),
(31, '朝ぼらけ有明の月と見るまでに', 'あさぼらけありあけのつきとみるまでに', '吉野の里に降れる白雪', 'よしののさとにふれるしらゆき', '坂上是則', 'さかのうえのこれのり', '夜がほのぼのと明ける頃、吉野の里に降った白い雪は、まるで空に残っている有明の月が光っているかのように見える。', '夜明けの情景を詠んだ歌です。吉野の山里に降り積もった雪が、まだ空に残る有明の月の光を反射して、幻想的な美しさを醸し出している様子を表現しています。'),
(32, '山川に風のかけたるしがらみは', 'やまがわにかぜのかけたるしがらみは', '流れもあへぬ紅葉なりけり', 'ながれもあえぬもみじなりけり', '春道列樹', 'はるみちのつらき', '山を流れる川に、風が作り上げた柵（しがらみ）のようなものがある。それは流れずに溜まっている、たくさんの紅葉であったよ。', '秋の山里の情景を詠んだ歌です。紅葉が風に吹かれて川に集まり、まるで川の流れを堰き止める柵のように見える様子を、比喩を用いて巧みに表現しています。'),
(33, 'ひさかたの光のどけき春の日に', 'ひさかたのひかりのどけきはるのひに', 'しづ心なく花の散るらむ', 'しずごころなくはなのちるらむ', '紀友則', 'きのとものり', '空の光がのどかな春の日に、どうして桜の花は落ち着きなく散ってしまうのだろうか。', '満開の桜が散っていく様子を詠んだ歌です。穏やかで平和な春の風景とは対照的に、せわしなく散り急ぐ桜に、作者の惜しむ気持ちが込められています。'),
(34, '誰をかも知る人にせむ高砂の', 'たれをかもしるひとにせんたかさごの', '松も昔の友ならなくに', 'まつもむかしのともならなくに', '藤原興風', 'ふじわらのおきかぜ', '誰を親しい人と呼べばよいのだろう。長生きすると言われる高砂の松でさえ、昔からの友達ではないというのに。', '長寿を象徴する高砂の松を引き合いに出し、昔からの知り合いや友人がいなくなってしまった寂しさを詠んだ歌です。年老いて孤独になった心情を表現しています。'),
(35, '人はいさ心も知らずふるさとは', 'ひとはいさこころもしらずふるさとは', '花ぞ昔の香ににほひける', 'はなぞむかしのかににおいける', '紀貫之', 'きのつらゆき', 'あなたの心はどうなっているかは知らないけれど、昔住んでいたこの故郷の梅の花は、昔と同じ香りで咲いている。', '久しぶりに故郷を訪れた際に詠んだ歌とされています。変わってしまった人の心とは対照的に、変わらない梅の花の香りに懐かしさと感慨を覚える様子を詠んでいます。'),
(36, '夏の夜はまだ宵ながら明けぬるを', 'なつのよはまだよいながらあけぬるを', '雲のいづこに月宿るらむ', 'くものいずこにつきやどるらん', '清原深養父', 'きよはらのふかやぶ', '夏の夜は、まだ宵の口だと思っていたのに、もう明けてしまった。あの月は雲のどのあたりに宿っているのだろうか。', '夏の短い夜の情景を詠んだ歌です。あっという間に夜が明けてしまった驚きと、空に残る月の行方を想像する様子を、簡潔かつ叙情的に表現しています。'),
(37, '白露に風の吹きしく秋の野は', 'しらつゆにかぜのふきしくあきののは', 'つらぬきとめぬ玉ぞ散りける', 'つらぬきとめぬたまぞちりける', '文屋朝康', 'ふんやのあさやす', '白露に風が頻繁に吹く秋の野は、まるで糸を通していない真珠の玉が散っているようだ。', '秋の野原の情景を詠んだ歌です。葉の上に光る朝露を真珠の玉に見立て、風に吹かれて露が散る様子を、糸を通していない玉がばらばらに散る様にたとえ、美しく描写しています。'),
(38, '忘らるる身をば思はず誓ひてし', 'わすらるるみをばおもはずちかひてし', '人の命の惜しくもあるかな', 'ひとのいのちのおしくもあるかな', '右近', 'うこん', '忘れられてしまう自分の身はどうなっても構わない。ただ、私を忘れないと誓ったあなたの命が、神罰で短くなってしまうことが惜しまれることだ。', '恋人に裏切られた女性の気持ちを詠んだ歌です。自分の悲しみよりも、嘘をついた相手の身を案じる、献身的で一途な愛情を表現しています。'),
(39, '浅茅生の小野の篠原しのぶれど', 'あさじふのおののしのはらしのぶれど', 'あまりてなどか人の恋しき', 'あまりてなどかひとのこいしき', '参議等', 'さんぎひとし', '浅茅が生えている小野の篠原で、忍び草（しのぶぐさ）が心の中で忍んでいるように、私は人に知られぬように恋心を隠そうとするけれど、やはり恋しさは募るばかりだ。', '恋の歌として詠まれました。「しのぶ」という言葉を重ねて、恋心を隠そうとする気持ちと、隠しきれない恋しさの深さを表現しています。'),
(40, '忍ぶれど色に出でにけりわが恋は', 'しのぶれどいろにいでにけりわがこいは', 'ものや思ふと人の問ふまで', 'ものやおもうとひとのとうまで', '平兼盛', 'たいらのかねもり', '人に知られぬよう隠していたけれど、私の恋心は顔に出てしまっていたようだ。とうとう「何か物思いにふけっているのですか」と人に尋ねられるほどに。', '恋の歌として詠まれました。隠そうとしても隠しきれない恋の苦しさを、客観的な視点からユーモラスに表現しています。'),
(41, '恋すてふわが名はまだき立ちにけり', 'こひすちょうわがなはまだきたちにけり', '人知れずこそ思ひそめしか', 'ひとしれずこそおもいそめしか', '壬生忠見', 'みぶのただみ', '恋をしているという私の噂は、こんなにも早く広まってしまったようだ。誰にも知られないように、ひそかに思い始めたばかりなのに。', '平兼盛の歌と対照的な恋の歌です。ひそかに抱いていた恋心が、いつの間にか人に知られてしまった驚きと戸惑いを詠んでいます。'),
(42, '契りきなかたみに袖をしぼりつつ', 'ちぎりきなかたみにそでをしぼりつつ', '末の松山波越さじとは', 'すえのまつやまなみこさじとは', '清原元輔', 'きよはらのもとすけ', '互いに涙で濡れた袖を絞りながら、「末の松山を波が越えることがないように、私たちの愛も永遠に変わらない」と誓い合ったのに。', '恋の歌として詠まれました。どんなことがあっても決して変わらない愛を誓い合ったのに、相手の心が離れてしまったことを嘆く歌です。'),
(43, '逢ひ見ての後の心にくらぶれば', 'あいみてののちのこころにくらぶれば', '昔はものを思はざりけり', 'むかしはものをおもわざりけり', '権中納言敦忠', 'ごんちゅうなごんあつただ', '実際に逢って結ばれてからの恋しい気持ちと比べると、逢う前の、ただ想像していただけの気持ちなど、まだ恋だとは言えなかったのだな。', '恋の歌として詠まれました。実際に恋しい人と結ばれて初めて、本当の恋の深さを知ったという、感動と驚きを表現しています。'),
(44, '逢ふことの絶えてしなくばなかなかに', 'あうことのたえてしなくばなかなかに', '人をも身をも恨みざらまし', 'ひとをもみをも恨みざらまし', '中納言朝忠', 'ちゅうなごんあさただ', 'もしあの人に逢うことが全くなかったなら、かえってあの人を恨んだり、わが身を嘆いたりすることはなかっただろうに。', '恋の歌として詠まれました。会えない苦しみから、いっそ最初から出会わなければよかった、という切ない気持ちを詠んでいます。'),
(45, 'あはれともいふべき人は思ほえで', 'あはれともいうべきひとはおもおえで', '身のいたづらになりぬべきかな', 'みのいたずらになりぬべきかな', '謙徳公', 'けんとくこう', '私のことを「かわいそうに」と言ってくれる人も思い浮かばないまま、私の人生はむなしく終わってしまうのだろうか。', '作者の孤独と悲しみを詠んだ歌です。恋人や友人もなく、自分の死を悲しんでくれる人もいない、という絶望的な孤独感を表現しています。'),
(46, '由良のとを渡る舟人かぢを絶え', 'ゆらのとをわたるふなびとかじをたえ', 'ゆくへも知らぬ恋の道かな', 'ゆくへもしらぬこいのみちかな', '曾禰好忠', 'そねのよしただ', '', ''),
(47, '八重葎しげれる宿のさびしきに', 'やえむぐらしげれるやどのさびしきに', '人こそ見えね秋は来にけり', 'ひとこそみえねあきはきにけり', '恵慶法師', 'えぎょうほうし', '八重葎（やえむぐら）が茂っている私の寂しい宿に、訪れる人は誰もいないけれど、秋だけはやって来たようだ。', '秋の孤独な情景を詠んだ歌です。草木が生い茂り、誰も訪れない荒れ果てた家でも、季節は巡って秋が来たことを静かに受け止める心情を表現しています。'),
(48, '風をいたみ岩うつ波のおのれのみ', 'かぜをいたみいわうつなみのおのれのみ', 'くだけてものを思ふころかな', 'くだけてものをおもうころかな', '源重之', 'みなもとのしげゆき', '風が激しいので岩に打ち砕かれる波のように、私だけが打ち砕かれるような思いで物思いにふけるこの頃だ。', '恋の歌として詠まれました。波が岩にぶつかって砕ける様子と、恋の苦しみで心が砕けるような自分の思いを重ねて表現しています。'),
(49, 'みかきもり衛士のたく火の夜は燃え', 'みかきもりえじのたくひのよるはもえ', '昼は消えつつものをこそ思へ', 'ひるはきえつつものをこそおもへ', '大中臣能宣朝臣', 'おおなかとみのよしのぶあそん', '宮中の警備にあたる衛士が夜に焚く火は燃え続けているが、昼には消えてしまうように、私の恋心は夜には燃え上がり、昼には消え入るようにもの思いにふけっている。', '恋の歌として詠まれました。昼夜で変化する衛士の焚き火の様子を、昼間は人目を気にして隠し、夜に燃え上がる自身の恋心にたとえています。'),
(50, '君がため惜しからざりし命さへ', 'きみがためおしからざりしいのちさえ', 'ながくもがなと思ひけるかな', 'ながくもがなとおもいけるかな', '藤原義孝', 'ふじわらのよしたか', 'あなたのために死んでも惜しくないと思っていたこの命でさえ、あなたと逢った今となっては、どうか長くあってほしいと思うようになった。', '恋の歌として詠まれました。恋人と結ばれる前は、恋のために死んでも構わないと思っていたが、いざ結ばれてみると、この幸せな日々を永遠に続けたいと願うようになった、という心情を表現しています。'),
(51, 'かくとだにえやはいぶきのさしも草', 'かくとだにえやわいぶきのさしもぐさ', 'さしも知らじな燃ゆる思ひを', 'さしもしらじなもゆるおもいを', '藤原実方朝臣', 'ふじわらのさねかたあそん', 'せめて「こうして恋しく思っています」とさえ言えないのだ。伊吹山のさしも草が燃えるように、こんなに燃え盛る私の思いを、あの人はそれほど（さしも）知らないだろうな。', '恋の歌として詠まれました。「伊吹（いぶき）」と「さしも（さしも草）」を言葉遊びのように使い、燃えるような恋心を相手に伝えられないもどかしさを表現しています。'),
(52, '明けぬれば暮るるものとは知りながら', 'あけぬればくるるものとはしりながら', 'なほ恨めしき朝ぼらけかな', 'なほうらめしきあさぼらけかな', '藤原道信朝臣', 'ふじわらのみちのぶあそん', '', ''),
(53, '嘆きつつひとり寝る夜の明くる間は', 'なげきつつひとりぬるよのあくるまは', 'いかに久しきものとかは知る', 'いかにひさしきものとかはしる', '右大将道綱母', 'うだいしょうみちつなのはは', '', ''),
(54, '忘れじのゆく末まではかたければ', 'わすれじのゆくすえまではかたければ', '今日を限りの命ともがな', 'きょうをかぎりのいのちともがな', '儀同三司母', 'ぎどうさんしのはは', '', ''),
(55, '滝の音は絶えて久しくなりぬれど', 'たきのおとはたえてひさしくなりぬれど', '名こそ流れてなほ聞こえけれ', 'なこそながれてなおきこえけれ', '大納言公任', 'だいなごんきんとう', '大覚寺の庭にある滝の音は、もう長い間止まってしまったけれど、その名声だけは今も変わらず世間に聞こえている。', '大覚寺の庭園にある人工の滝が枯れてしまった様子を詠んだ歌です。水の音は消えても、その美しさを讃える「名」は永遠に残るという、趣深い歌です。'),
(56, 'あらざらむこの世のほかの思ひ出に', 'あらざらんこのよのほかのおもいでに', '今ひとたびの逢ふこともがな', 'いまひとたびのおうこともがな', '和泉式部', 'いずみしきぶ', 'もうこの世に生きていないであろう未来の思い出として、もう一度、あなたに逢いたいものです。', '病に伏していた際に詠まれた歌とされています。もうすぐ死んでしまうかもしれないという状況で、死後の思い出としてでも、恋しい人と会いたいと願う、悲痛なまでの切ない恋心を表現しています。'),
(57, 'めぐり逢ひて見しやそれともわかぬ間に', 'めぐりあいてみしやそれともわかぬまに', '雲隠れにし夜半の月かな', 'くもがくれにしよわのつきかな', '紫式部', 'むらさきしきぶ', '久しぶりに再会したのに、それがあなたかどうか見分けがつかないうちに、雲に隠れてしまった夜中の月のような人だなぁ。', '幼なじみと再会した際に詠んだ歌とされています。再会した喜びも束の間、すぐに別れてしまった寂しさ、そして変わり果てた姿に気づかずに別れてしまったことを嘆く歌です。'),
(58, '有馬山猪名の笹原風吹けば', 'ありまやまいなのささはらかぜふけば', 'いでそよ人を忘れやはする', 'いでそよひとをわすれやわする', '大弐三位', 'だいにのさんみ', '有馬山の猪名（いな）の笹原に風が吹くと、ざわざわと音を立てる。「いな（いやだ）」と言うように、どうしてあなたのことを忘れたりするでしょうか、いいえ、忘れません。', '恋の歌として詠まれました。「猪名（いな）」と「否（いな）＝いやだ」を、「笹（ささ）」の葉が「そよ」と音を立てる様子を重ね、恋人を忘れることなど決してないと誓う歌です。'),
(59, 'やすらはで寝なましものを小夜更けて', 'やすらわでねなましものをさよふけて', 'かたぶくまでの月を見しかな', 'かたぶくまでのつきをみしかな', '赤染衛門', 'あかぞめえもん', 'もしあなたが来ないなら、ためらわずに寝てしまえばよかったのに。夜が更け、月が西に傾くまで起きて待ってしまったことだ。', '恋の歌として詠まれました。恋人が来るのを期待して待っていたが、結局来なかった。しかしそのおかげで、美しい月を見ることができた、というような、諦めとわずかな美しさを見出す心情を表現しています。'),
(60, '大江山いく野の道の遠ければ', 'おおえやまいくののみちのとおければ', 'まだふみも見ず天の橋立', 'まだふみもみずあまのはしだて', '小式部内侍', 'こしきぶのないし', '大江山や生野を通る道は遠いので、まだ母から手紙（ふみ）も見ていませんし、天の橋立も見ていません。', '藤原定頼との歌合わせで詠んだ歌です。定頼が「お前の歌は母（和泉式部）が代作したのだろう」と皮肉を言ったのに対し、「母の手紙（ふみ）が届かないほど遠いので、天の橋立（母がいる場所）も見ていません」と、機転を利かして見事に返した歌として有名です。'),
(61, 'いにしへの奈良の都の八重桜', 'いにしえのならのみやこのやえざくら', '今日九重ににほひぬるかな', 'きょうここのへににおいぬるかな', '伊勢大輔', 'いせのたいふ', '昔の奈良の都で咲いていた八重桜が、今日、この京都の宮中（九重）で美しく咲き誇っていることだ。', '後一条天皇が即位した際、前栽（庭）の桜が美しいので歌を詠むよう命じられた際に、作者が即座に詠んだ歌。奈良の都から移り住んだ八重桜を、新しい都の繁栄に重ね合わせています。'),
(62, '夜をこめて鳥のそらねははかるとも', 'よをこめてとりのそらねははかるとも', 'よに逢坂の関は許さじ', 'よにおうさかのせきはゆるさじ', '清少納言', 'せいしょうなごん', '夜がまだ明けきらないうちに、鶏の鳴き声を真似て、あなたをだまして外へ出そうとしても、この逢坂の関は決して許さないでしょう。', '清少納言が、恋人が夜明け前に帰るのを引き留めるために詠んだ歌とされています。鶏の鳴き真似で関所を開かせた孟嘗君の故事を引用し、機知に富んだやり取りを歌にしています。'),
(63, '今はただ思ひ絶えなむとばかりを', 'いまはただおもひたえなんとばかりを', '人づてならで言ふよしもがな', 'ひとづてならでいうよしもがな', '左京大夫道雅', 'さきょうのだいぶみちまさ', 'もうあなたへの思いを断ち切ってしまおう、とただそれだけを、人づてではなく直接あなたに伝える方法があればいいのに。', '恋の歌として詠まれました。恋が実らない苦しみから、いっそ諦めようと思う一方で、その決心を直接伝えられないもどかしさを表現しています。'),
(64, '朝ぼらけ宇治の川霧たえだえに', 'あさぼらけうじのかわぎりたえだえに', 'あらはれわたる瀬々の網代木', 'あらわれわたるせぜのあじろぎ', '権中納言定頼', 'ごんちゅうなごんさだより', '夜がほのぼのと明ける頃、宇治川にかかった川霧が途切れ途切れになり、川の浅瀬に立てられた網代木（あじろぎ）が姿を現していく。', '冬の宇治川の情景を詠んだ歌です。明け方の霧が晴れていく様子と、その中に網代木が徐々に見えてくる様子を、絵画のように美しく描写しています。'),
(65, '恨みわびほさぬ袖だにあるものを', 'うらみわびほさぬそでだにあるものを', '恋に朽ちなん名こそ惜しけれ', 'こいにくちなんなこそおしけれ', '相模', 'さがみ', '恨み嘆いて乾く間もないほどに濡れる私の袖があるのに、この恋のせいで世間に広まってしまう私の悪い評判が惜しい。', '恋の苦しみを詠んだ歌です。恋のせいで涙が絶えない悲しさよりも、この恋が原因で世間に悪評が立つことへの悔しさを表現しています。'),
(66, 'もろともにあはれと思へ山桜', 'もろともにあわれとおもえやまざくら', '花よりほかに知る人もなし', 'はなよりほかにしるひともなし', '大僧正行尊', 'だいそうじょうぎょうそん', '私と一緒に、この世の儚さをしみじみと感じておくれ、山桜よ。この山奥では、花以外に私の気持ちを分かってくれる人はいないのだから。', '修行で山にこもった際に詠んだ歌とされています。山奥で孤独に暮らす中で、咲いている山桜だけが自分の気持ちを理解してくれる友である、という心情を表現しています。'),
(67, '春の夜の夢ばかりなる手枕に', 'はるのよのゆめばかりなるたまくらに', 'かひなく立たむ名こそ惜しけれ', 'かいなくたたんなこそおしけれ', '周防内侍', 'すおうのないし', '春の夜の夢のように短い手枕（恋人の腕枕）で、つまらない評判が立ってしまうのは残念なことだ。', '宮中で男性に冗談で腕枕をされた際に詠んだ歌です。一晩限りの遊びで、世間に噂が立ってしまうのはもったいない、という女性の機知に富んだ心を表現しています。'),
(68, '心にもあらで憂き世にながらへば', 'こころにもあらでうきよにながらえば', '恋しかるべき夜半の月かな', 'こいしかるべきよわのつきかな', '三条院', 'さんじょういん', '自分の思い通りにならないつらい世の中に、もしも長生きしてしまったならば、今見ているこの夜中の月が、懐かしく思われるのだろうか。', '病に苦しんでいた際に詠まれた歌です。いつ死んでもおかしくない状況で、もし生きながらえたら、今経験しているこの苦しい日々や、それを見守る月すら懐かしく思うのかもしれない、という複雑な心情を表現しています。'),
(69, '嵐吹く三室の山の紅葉葉は', 'あらしふくみむろのやまのもみじばは', '竜田の川の錦なりけり', 'たつたのかわのにしきなりけり', '能因法師', 'のういんほうし', '嵐が吹く三室山から散った紅葉の葉は、竜田川に流れて、美しい錦のようだ。', '秋の紅葉の名所を詠んだ歌です。竜田川の川面に散り敷いた紅葉を、まるで織物のような錦にたとえ、その見事な美しさを表現しています。'),
(70, 'さびしさに宿を立ち出でて眺むれば', 'さびしさにやどをたちいでてながむれば', 'いづくも同じ秋の夕暮れ', 'いずくもおなじあきのゆうぐれ', '良暹法師', 'りょうぜんほうし', '寂しさに耐えかねて家を出て、あたりを見渡してみると、どこも同じように物悲しい秋の夕暮れだ。', '秋の寂しさを詠んだ歌です。どこへ行っても変わらない寂しさがあることに気づき、より一層孤独を感じる、秋の夕暮れの心象風景を表現しています。'),
(71, '夕されば門田の稲葉おとづれて', 'ゆうさればかどたのいなばおとずれて', '蘆のまろやに秋風ぞ吹く', 'あしのまろやにあきかぜぞふく', '大納言経信', 'だいなごんつねのぶ', '夕方になると、門前の田んぼの稲の葉を揺らしながら、秋風が私の粗末な庵を訪れてくる。', '秋の風情を詠んだ歌です。「風」を「訪れる」と擬人化することで、寂しさの中にも親しみを感じさせる情景を表現しています。'),
(72, '音に聞く高師の浜のあだ波は', 'おとにきくたかしのはまのあだなみは', 'かけじや袖の濡れもこそすれ', 'かけじやそでのぬれもこそすれ', '祐子内親王家紀伊', 'ゆうしないしんのうけのきい', '噂に聞く、高師の浜のいたずらな波のように、浮気なあなたの噂にはかかわりません。もし関わったら、私の袖が濡れてしまう（涙や悪評で苦労する）といけないから。', '恋の歌として詠まれました。「高師（たかし）」と「高し」、「あだ波（あだなみ）」と「あだ名（あだな）」をかけて、遊び上手な男性に用心する女性の気持ちを表現しています。'),
(73, '高砂の尾の上の桜咲きにけり', 'たかさごのおのへのさくらさきにけり', '外山の霞たたずもあらなむ', 'とやまのかすみたたずもあらなん', '権中納言匡房', 'ごんちゅうなごんまさふさ', '高砂の峰の桜が咲いたことだ。その美しい姿を隠さないように、麓の霞よ、どうか立たないでおくれ。', '春の桜の美しさを詠んだ歌です。峰に咲く桜の絶景を一目見たいと願う気持ちが、霞を擬人化して「隠さないでくれ」と懇願する表現に込められています。'),
(74, '憂かりける人を初瀬の山おろしよ', 'うかりけるひとをはつせのやまおろしよ', '激しかれとは祈らぬものを', 'はげしかれとはいのらぬものを', '源俊頼朝臣', 'みなもとのとしよりあそん', 'この世の中は永遠に変わらずにあってほしいものだ。渚を漕ぎ進む漁師の小舟が、綱をたぐり寄せるその光景が、しみじみと心に染みる。', '日常生活のささやかな情景を詠んだ歌です。漁師の営みという変わらない日常の中に、永遠に続いてほしいという願いを見出す、作者の深い感性を表現しています。'),
(75, '契りおきしさせもが露を命にて', 'ちぎりおきしさせもがつゆをいのちにて', 'あはれ今年の秋もいぬめり', 'あはれことしのあきもいぬめり', '藤原基俊', 'ふじわらのもととし', 'あなたと約束した「させも草（さしもぐさ）の露」のように、儚い命を頼りとしていたのに、ああ、今年の秋も行ってしまったようだ。', '恋の歌として詠まれました。「させも草」の露がすぐに消えるように、約束が果たされないまま時が過ぎていくことを嘆く歌です。'),
(76, 'わたの原漕ぎ出でて見ればひさかたの', 'わたのはらこぎいでてみればひさかたの', '雲居にまがふ沖つ白波', 'くもいにまごうおきつしらなみ', '法性寺入道前関白太政大臣', 'ほっしょうじにゅうどうさきのかんぱくだいじょうだいじん', '広々とした大海原へ船を漕ぎ出して見渡すと、遠くの沖の白波が、空の雲と見間違えるほど白く輝いている。', '航海の景色を詠んだ歌です。どこまでも続く大海原の雄大さと、その彼方に雲のように見える白波の美しさを表現しています。'),
(77, '瀬をはやみ岩にせかるる滝川の', 'せをはやみいわにせかるるたきがわの', 'われても末に逢はむとぞ思ふ', 'われてもすえにあわんとぞおもう', '崇徳院', 'すとくいん', '流れが速いので岩に堰き止められて二つに分かれてしまう滝川が、また一つになるように、今は別れてもいつか必ずあなたと再会しよう。', '恋の歌としても、人生の歌としても解釈されます。一時的に分かたれても、いつか必ず再会できるという強い意志を、川の流れにたとえて表現しています。'),
(78, '淡路島かよふ千鳥の鳴く声に', 'あはじしまかようちどりのなくこえに', '幾夜寝覚めぬ須磨の関守', 'いくよねざめぬすまのせきもり', '源兼昌', 'みなもとのかねまさ', '淡路島と行き来する千鳥の鳴き声に、須磨の関守はどれほどの夜、目を覚ましたことだろうか。', '旅情を誘う歌です。海辺で鳴く千鳥の声を孤独な関守の耳に届けることで、その寂しさや物悲しさを情感豊かに表現しています。'),
(79, '秋風にたなびく雲の絶え間より', 'あきかぜにたなびくくものたえまより', 'もれいづる月の影のさやけさ', 'もれいづるつきのかげのさやけさ', '左京大夫顕輔', 'さきょうのだいぶあきすけ', '', ''),
(80, '長からむ心もしらず黒髪の', 'ながからんこころもしらずくろかみの', '乱れて今朝はものをこそ思へ', 'みだれてけさはものをこそおもえ', '待賢門院堀河', 'たいけんもんいんのほりかわ', 'あなたの心が長く私のもとにとどまるかどうかわからないまま、私の黒髪のように心が乱れて、今朝はもの思いにふけっています。', '恋の歌として詠まれました。長く美しい黒髪が乱れているように、相手の気持ちが分からず、心が乱れてもの思いにふける女性の心情を表現しています。'),
(81, 'ほととぎす鳴きつる方をながむれば', 'ほととぎすなきつるかたをながむれば', 'ただ有明の月ぞ残れる', 'ただありあけのつきぞのこれる', '後徳大寺左大臣', 'ごとくだいじのさだいじん', 'ホトトギスの声が聞こえた方角を眺めると、姿は見えず、ただ夜明けの空に月が残っているだけだ。', '夏の夜明け、ホトトギスの鳴き声を聞いて姿を探すが、見つからず月だけが残っている情景を詠んだ歌です。ホトトギスの声の儚さを感じさせる歌として知られています。'),
(82, '思ひわびさても命はあるものを', 'おもいわびさてもいのちはあるものを', '憂きにたへぬは涙なりけり', 'うきにたえぬはなみだなりけり', '道因法師', 'どういんほうし', '恋に悩み、どうしようもなくつらいけれど、命はある。しかし、つらい気持ちに耐えられないのは、とめどなく流れる涙だ。', '恋の苦しみを詠んだ歌です。いくらつらくても命が尽きることはないが、流れる涙だけはどうにもならないという、やりきれない思いを表現しています。'),
(83, '世の中よ道こそなけれ思ひ入る', 'よのなかのみちこそなけれおもいいる', '山の奥にも鹿ぞ鳴くなる', 'やまのおくにもしかぞなくなる', '皇太后宮大夫俊成', 'こうたいごうぐうのだいぶとしなり', 'この世は、世俗から逃れる道はないものだなぁ。世を憂いて深く分け入った山の奥にさえ、物悲しく鳴く鹿の声が聞こえてくる。', '世を憂いて隠遁生活に入ろうとする心境を詠んだ歌です。俗世を離れてもなお、鹿の声が聞こえるように寂しさや世の憂いはついてくることを示しています。'),
(84, 'ながらへばまたこの頃やしのばれむ', 'ながらえばまたこのごろやしのばれん', '憂しと見し世ぞ今は恋しき', 'うしとみしよぞいまはこいしき', '藤原清輔朝臣', 'ふじわらのきよすけあそん', 'もし長生きすれば、今この苦しい日々も懐かしく思われるのだろうか。つらいと思っていた昔の世が、今となっては恋しいように。', '人生の苦楽を達観した歌です。過去の苦しかった日々が懐かしいように、今のつらい日々もいつか懐かしく思える日が来るだろう、という深い感慨を表現しています。'),
(85, '夜もすがら物思ふころは明けやらで', 'よもすがらものおもうころはあけやらで', '閨のひまさへつれなかりけり', 'ねやのひまさえつれなかりけり', '俊恵法師', 'しゅんえほうし', '', ''),
(86, '嘆けとて月やは物を思はする', 'なげけとてつきやはものをおもわする', 'かこち顔なる我が涙かな', 'かこちがおなるわがなみだかな', '西行法師', 'さいぎょうほうし', '嘆き悲しめと、月が私に物思いをさせているわけではないのに、まるで月のせいだとでも言うように、私の涙がこぼれ落ちることだ。', '月を見て物思いにふける心情を詠んだ歌です。月はただそこにあるだけなのに、勝手に悲しんでしまう自分を客観的に見つめ、その涙を月に向かって言い訳しているような、ユーモラスで哲学的な一面を持っています。'),
(87, '村雨の露もまだひぬ槇の葉に', 'むらさめのつゆもまだひぬまきのはに', '霧立ちのぼる秋の夕暮れ', 'きりたちのぼるあきのゆうぐれ', '寂蓮法師', 'じゃくれんほうし', 'にわか雨（村雨）の露もまだ乾いていない槇（まき）の葉に、霧が立ち上っていく、物寂しい秋の夕暮れだ。', '秋の夕暮れの情景を詠んだ歌です。雨上がりと霧の組み合わせが、湿った空気と冷え込みを感じさせ、物悲しさを一層深めています。'),
(88, '難波江の葦のかりねのひとよゆゑ', 'なにわえのあしのかりねのひとよゆえ', 'みをつくしてや恋ひわたるべき', 'みをつくしてやこいわたるべき', '皇嘉門院別当', 'こうかもんいんのべっとう', '難波江の葦の仮寝（かりね）のような一夜限りの関係のために、身を尽くして（みをつくし）こんなにも長く恋し続けなければならないのでしょうか。', '恋の歌として詠まれました。「難波江」と「葦のかりね」、「澪標（みおつくし）」と「身を尽くし」を掛けて、一夜だけの関係に深く苦しむ女性の心情を表現しています。'),
(89, '玉の緒よ絶えなば絶えねながらへば', 'たまのおよたえなばたえねながらへば', 'しのぶることの弱りもぞする', 'しのぶることのよわりもぞする', '式子内親王', 'しょくしないしんのう', '私の命よ、絶えるなら絶えてしまえ。このまま長らえてしまうと、人に知られぬように隠している恋心が、人にもれてしまいそうだから。', '恋の苦しみを詠んだ歌です。命が尽きてしまう方が、ひそかに抱いている恋心が知られてしまうよりましだ、という、切なくも潔い決意を表現しています。'),
(90, '見せばやな雄島のあまの袖だにも', 'みせばやなおじまのあまのそでだにも', '濡れにぞ濡れし色は変らず', 'ぬれにぞぬれしいろはかわらず', '殷富門院大輔', 'いんぷもんいんのたいふ', '見せてやりたいものだ、雄島（おじま）の漁師の袖でさえ、こんなにも濡れているけれど、色が変わらないということを。', '恋の歌として詠まれました。雄島の漁師の袖は潮に濡れても色あせないように、自分の袖（涙）は濡れても、あなたの心変わりを嘆く気持ちは変わらない、という強い思いを表現しています。'),
(91, 'きりぎりす鳴くや霜夜のさむしろに', 'きりぎりすなくやしもよのさむしろに', '衣かたしきひとりかも寝む', 'ころもかたしきひとりかもねん', '後京極摂政前太政大臣', 'ごきょうごくせっしょうさきのだいじょうだいじん', 'コオロギ（きりぎりす）が鳴いている霜が降りる寒い夜、私は衣の片袖だけを敷いて、一人で寝ることになるのだろうか。', '秋の夜長の孤独を詠んだ歌です。寒々とした情景と、一人で寝る寂しさを「衣かたしき」という表現で、巧みに描写しています。'),
(92, '我が袖は潮干に見えぬ沖の石の', 'わがそではしおひにみえぬおきのいしの', '人こそ知らね乾く間もなし', 'ひとこそしらねかわくまもなし', '二条院讃岐', 'にじょういんのさぬき', '私の袖は、潮が引いても見えない沖の岩のように、人には知られないけれど、乾く暇がないほどに涙で濡れている。', '人知れず泣き続けている孤独な恋の苦しみを詠んだ歌です。沖の岩が常に潮に濡れているように、絶え間なく涙を流す様子を表現しています。'),
(93, '世の中は常にもがもな渚こぐ', 'よのなかはつねにもがもななぎさこぐ', 'あまの小舟の綱手かなしも', 'あまのこぶねのつなでかなしも', '鎌倉右大臣', 'かまくらのうだいじん', 'この世はいつまでも変わらずにあってほしいものだ。渚を漕いでいく漁師の小舟が綱をたぐり寄せる光景が、しみじみと心に染みる。', '源実朝が詠んだとされる歌です。漁師の営みという変わらない日常の中に、永遠に続いてほしいという願いを見出す、静かで深い心情を表現しています。'),
(94, 'み吉野の山の秋風さ夜ふけて', 'みよしののやまのあきかぜさよふけて', 'ふるさと寒く衣うつなり', 'ふるさむくころもうつなり', '参議雅経', 'さんぎまさつね', '吉野の山の秋風が夜深く吹いて、ふるさとが寒くなり、衣を打つ音が聞こえてくる。', '秋の夜長の情景を詠んだ歌です。故郷に響く衣を打つ音が、寒々しい秋の夜の雰囲気を一層強調し、物悲しさを誘います。'),
(95, 'おほけなく憂き世の民におほふかな', 'おおけなくうきよのたみにおおうかな', 'わが立つそまに墨染の袖', 'わがたつそまにすみぞめのそで', '前大僧正慈円', 'さきのだいそうじょうじえん', '', ''),
(96, '花さそふ嵐の庭の雪ならで', 'はなさそふあらしのにわのゆきならで', 'ふりゆくものはわが身なりけり', 'ふりゆくものはわがみなりけり', '入道前関白太政大臣', 'にゅうどうさきのだいじょうだいじん', '', ''),
(97, '来ぬ人を松帆の浦の夕なぎに', 'こぬひとをまつほのうらのゆうなぎに', '焼くや藻塩の身もこがれつつ', 'やくやもしおのみもこがれつつ', '権中納言定家', 'ごんちゅうなごんさだいえ', '来ない人を松帆の浦で待っていると、夕方の潮が引いた海辺で藻塩を焼くように、私の身も焦がれていく。', '恋の歌として詠まれました。「待つ」と「松帆（まつほ）」を、「身を焦がす」と「藻塩（もしお）を焼く」をかけて、恋しい人を待つ苦しさを表現しています。'),
(98, '風そよぐならの小川の夕暮は', 'かぜそよぐならのおがわのゆうぐれは', 'みそぎぞ夏のしるしなりける', 'みそぎぞなつのしるしなりける', '従二位家隆', 'じゅにいいえたか', '', ''),
(99, '人も惜し人も恨めしあぢきなく', 'ひともおしひともうらめしあじきなく', '世を思ふゆゑにもの思ふ身は', 'よをおもうゆえにものおもふみは', '後鳥羽院', 'ごとばいん', '', ''),
(100, '百敷や古き軒端のしのぶにも', 'ももしきやふるきのきばのしのぶにも', 'なおあまりある昔なりけり', 'なほあまりあるむかしなりけり', '順徳院', 'じゅんとくいん', '', '');

-- テーブルの作成
CREATE TABLE ikai (
    ikai_id INT PRIMARY KEY AUTO_INCREMENT,
    ikai_name VARCHAR(50) NOT NULL,
    ikai_yomi VARCHAR(50) NOT NULL,
    kaisetsu TEXT
);

-- レコードの追加
INSERT INTO ikai (ikai_id, ikai_name, ikai_yomi, kaisetsu) VALUES
(1, '地下人', 'じげにん', '地上（殿上）に昇ることを許されず、清涼殿の外で勤務した官人の総称です。主に六位以下の位階を持つ下級貴族や下級官人などがこれに当たります。'),
(2, '諸大夫', 'しょだいぶ', '五位の位階を持つ官人の総称です。公卿と地下人の中間に位置し、地方官など重要な役職に就いていました。'),
(3, '公卿', 'くぎょう', '律令制において、三位以上の位階を持つ高位の官人の総称です。彼らは殿上人（てんじょうびと）でもあり、朝廷の重要な政務を担いました。'),
(4, '太政大臣', 'だじょうだいじん', '律令制において、朝廷の最高官職です。常設の役職ではありませんでしたが、摂関に次ぐ権威を持っていました。'),
(5, '摂政・関白', 'せっしょう・かんぱく', '天皇が幼少または病弱なときに、天皇に代わって政治を行うのが摂政です。また、成人した天皇を補佐して政治を行うのが関白です。これらは、藤原氏が代々独占し、政治の実権を握っていました。');

